import React, {Component} from 'react';
import './Main.css';
import {FaPlus} from 'react-icons/fa';
import {FaEdit, FaWindowClose} from 'react-icons/fa';

export default class Main extends Component{
  state ={
    newTask: " ",
    tasks:[]
  }

  changeSubmit = (e) => {
    e.preventDefault();
    const {tasks} = this.state;
    let {newTask} = this.state;
    newTask = newTask.trim()

    if(tasks.indexOf(newTask) !== -1) return;

    const newtasks = [...tasks];

    this.setState({
      tasks: [...newtasks, newTask],
      newTask: " ",
    })
  }

  changeInput= (e) => {
    this.setState({
      newTask:e.target.value,
    })
  }

  editTask= (e,index) => {
    console.log('Editei o indice', index)
  }

  deleteTask= (e,index) => {
    const {tasks} = this.state;
    const newtasks = [...tasks];
    newtasks.splice(index, 1)

    this.setState({
      tasks: [...newtasks],
    })
  }

  render(){

    const {newTask,tasks} = this.state

    return(
      <div className='Main'>
         <h1>Task List</h1>

          <form onSubmit={this.changeSubmit} className='form'>
          <input
          type="text"
          onChange={this.changeInput}
          value={newTask} />

         <button type="submit">
          <FaPlus/>
         </button>
          </form>

          <ul className='Tasks'>
          {tasks.map((task,index)=>(
              <li key={task}>
                {task}
               <div>
                <FaEdit className='edit'onClick={(e)=>this.editTask(e,index)}/>
                <FaWindowClose className='delete' onClick={(e)=>this.deleteTask(e,index)}/>
               </div>
                </li>
            ))}
          </ul>

      </div>
    )
  }
}
