import React, {Component} from 'react';
import './Main.css';
import {FaPlus} from 'react-icons/fa';
import {FaEdit, FaWindowClose} from 'react-icons/fa';

export default class Main extends Component{
  state ={
    newTask: " ",
    tasks:[]
  }

  changeSubmit = (e) => {
    e.preventDefault();
    const {tasks,index} = this.state;
    let {newTask} = this.state;
    newTask = newTask.trim()

    if(tasks.indexOf(newTask) !== -1) return;

    const newtasks = [...tasks];

  if(index == -1){
    this.setState({
      tasks: [...newtasks, newTask],
      newTask: " ",
    })
  }else{
    newtasks[index] = newTask

    this.setState({
      tasks: [...newtasks],
      index: -1,
    })
  }

  }

  changeInput= (e) => {
    this.setState({
      newTask:e.target.value,
    })
  }

  editTask= (e,index) => {
    const {tasks} = this.state;

    this.setState({
      index,
      newTask: tasks[index],
    })
  }

  deleteTask= (e,index) => {
    const {tasks} = this.state;
    const newtasks = [...tasks];
    newtasks.splice(index, 1)

    this.setState({
      tasks: [...newtasks],
    })
  }

  render(){

    const {newTask,tasks} = this.state

    return(
      <div className='Main'>
         <h1>Task List</h1>

          <form onSubmit={this.changeSubmit} className='form'>
          <input
          type="text"
          onChange={this.changeInput}
          value={newTask} />

         <button type="submit">
          <FaPlus/>
         </button>
          </form>

          <ul className='Tasks'>
          {tasks.map((task,index)=>(
              <li key={task}>
                {task}
               <div>
               <input type="checkbox" cla></input>
                <FaEdit className='edit' onClick={(e)=>this.editTask(e,index)}/>
                <FaWindowClose className='delete' onClick={(e)=>this.deleteTask(e,index)}/>
               </div>
                </li>
            ))}
          </ul>

      </div>
    )
  }
}
