import React, {Component} from 'react';
import './Main.css';
import {FaPlus} from 'react-icons/fa';

export default class Main extends Component{
  state ={
    newTask: " ",
    tasks:[
      'Jogar no PC',
      'beber agua',
      'estudar'
    ]
  }
  changeInput= (e) => {
    this.setState({
      newTask:e.target.value,
    })
  }

  render(){

    const {newTask,tasks} = this.state

    return(
      <div className='Main'>
         <h1>Task List</h1>

          <form className='form'>
          <input
          type="text"
          onChange={this.changeInput}
          value={newTask} />

         <button type="submit">
          <FaPlus/>
         </button>

        <ul className='tasks'></ul>
            {tasks.map((task)=>(
              <li key={tasks}>{task}</li>
            ))}
          </form>
      </div>
    )
  }
}
