import React, {Component} from 'react';
import './main.css';

export default class Main extends Component{
  state ={
    newTask: " ",
  }
  changeInput= (e) => {
    this.setState({
      newTask:e.target.value,
    })
  }

  render(){

    return(

      <div className='Main'>
         <h1>Task List</h1>

         <input type="text" onChange={this.changeInput} />
         <button type="submit">Add</button>
      </div>
    )
  }
}
