import React, {Component} from 'react';
import './Main.css';
import {FaPlus} from 'react-icons/fa';

export default class Main extends Component{
  state ={
    newTask: " ",
  }
  changeInput= (e) => {
    this.setState({
      newTask:e.target.value,
    })
  }

  render(){

    return(
      <div className='Main'>
         <h1>Task List</h1>

          <form>
          <input type="text" onChange={this.changeInput} />
         <button type="submit">
          <FaPlus/>
         </button>
          </form>
      </div>
    )
  }
}
